from dataclasses_json import dataclass_json, LetterCase
from phonepe.sdk.pg.payments.v1.models.request.auth_mode import AuthMode
from dataclasses import dataclass, field

from phonepe.sdk.pg.payments.v1.models.request.payment_instrument import PaymentInstrument
from phonepe.sdk.pg.payments.v1.models.request.pg_instrument_type import PgInstrumentType
from phonepe.sdk.pg.payments.v1.models.request.token_details import TokenDetails


@dataclass_json(letter_case=LetterCase.CAMEL)
@dataclass
class TokenPaymentInstrument(PaymentInstrument):
    token_details: TokenDetails = field(default=None)
    type: str = field(default=PgInstrumentType.TOKEN.value)
    auth_mode: str = field(default=AuthMode.THREE_D_SECURE.value)
